// src/App.js
import React, { useState } from "react";
import { Card, CardHeader, CardTitle } from "./components/ui/card/card";
import Navigation from "./components/layout/Navigation";
import ReservationForm from "./components/reservation/ReservationForm";
import ReservationList from "./components/reservation/ReservationList";
import EditReservation from "./components/reservation/EditReservation";
import DeleteReservation from "./components/reservation/DeleteReservation";
import { Plus, Search, Edit, Trash2 } from "lucide-react";

function App() {
  const [activeTab, setActiveTab] = useState("create");

  // Configuration des onglets
  const tabs = [
    {
      id: "create",
      icon: <Plus className="h-4 w-4" />,
      label: "Nouvelle",
    },
    {
      id: "view",
      icon: <Search className="h-4 w-4" />,
      label: "Consulter",
    },
    {
      id: "edit",
      icon: <Edit className="h-4 w-4" />,
      label: "Modifier",
    },
    {
      id: "delete",
      icon: <Trash2 className="h-4 w-4" />,
      label: "Supprimer",
    },
  ];

  // Rendu des onglets
  const renderTabs = () => {
    return (
      <div className="flex space-x-4 mb-8 px-6">
        {tabs.map(({ id, icon, label }) => (
          <button
            key={id}
            onClick={() => setActiveTab(id)}
            className={`flex items-center space-x-2 px-6 py-3 rounded-xl transition-all transform hover:scale-105 ${
              activeTab === id
                ? "bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg"
                : "bg-white text-gray-700 hover:shadow-md"
            }`}
          >
            {icon}
            <span>{label}</span>
          </button>
        ))}
      </div>
    );
  };

  // Rendu du contenu en fonction de l'onglet actif
  const renderContent = () => {
    switch (activeTab) {
      case "create":
        return <ReservationForm />;
      case "view":
        return <ReservationList />;
      case "edit":
        return <EditReservation />;
      case "delete":
        return <DeleteReservation />;
      default:
        return <ReservationForm />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50">
      {/* Navigation */}
      <Navigation />

      {/* Contenu principal */}
      <main className="max-w-7xl mx-auto py-8 px-4">
        <Card className="w-full bg-white/80 backdrop-blur-sm">
          {/* En-tête */}
          <CardHeader className="border-b border-purple-100">
            <CardTitle className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 text-transparent bg-clip-text">
              Système de Réservation
            </CardTitle>
            <p className="text-gray-500 mt-1">
              Gérez vos réservations d'hôtel en toute simplicité
            </p>
          </CardHeader>

          {/* Onglets */}
          {renderTabs()}

          {/* Contenu dynamique */}
          <div className="transition-all duration-300 ease-in-out">
            {renderContent()}
          </div>
        </Card>
      </main>

      {/* Footer */}
      <footer className="py-4 text-center text-gray-600">
        <p>&copy; 2024 Système de Réservation d'Hôtel. Tous droits réservés.</p>
      </footer>
    </div>
  );
}

export default App;
