// src/components/reservation/ReservationList.jsx
import React from "react";
import { Card, CardContent } from "../ui/card/card";
import { Calendar, User, Bed, Phone, Mail } from "lucide-react";

const ReservationList = () => {
  // Données de test plus complètes
  const reservations = [
    {
      id: 1,
      clientName: "John Doe",
      email: "john@example.com",
      phone: "0612345678",
      roomType: "Double",
      startDate: "2024-12-01",
      endDate: "2024-12-05",
    },
    {
      id: 2,
      clientName: "Jane Smith",
      email: "jane@example.com",
      phone: "0687654321",
      roomType: "Suite Luxe",
      startDate: "2024-12-10",
      endDate: "2024-12-15",
    },
    {
      id: 3,
      clientName: "Alice Johnson",
      email: "alice@example.com",
      phone: "0699887766",
      roomType: "Simple",
      startDate: "2024-12-20",
      endDate: "2024-12-22",
    },
  ];

  return (
    <div className="p-6">
      <div className="grid gap-4">
        {/* En-tête */}
        <div className="grid grid-cols-5 gap-4 mb-2 px-4 text-sm font-semibold text-gray-600">
          <div>Client</div>
          <div>Contact</div>
          <div>Chambre</div>
          <div>Dates</div>
          <div>Actions</div>
        </div>

        {/* Liste des réservations */}
        {reservations.map((reservation) => (
          <div
            key={reservation.id}
            className="p-4 bg-white border border-purple-100 rounded-lg hover:border-purple-500 transition-all shadow-sm"
          >
            <div className="grid grid-cols-5 gap-4 items-center">
              {/* Information client */}
              <div className="flex items-center space-x-3">
                <User className="h-5 w-5 text-purple-500" />
                <span className="font-medium">{reservation.clientName}</span>
              </div>

              {/* Contact */}
              <div className="space-y-1 text-sm">
                <div className="flex items-center space-x-2">
                  <Mail className="h-4 w-4 text-gray-400" />
                  <span>{reservation.email}</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Phone className="h-4 w-4 text-gray-400" />
                  <span>{reservation.phone}</span>
                </div>
              </div>

              {/* Type de chambre */}
              <div className="flex items-center space-x-2">
                <Bed className="h-5 w-5 text-blue-500" />
                <span className="font-medium">{reservation.roomType}</span>
              </div>

              {/* Dates */}
              <div className="flex items-center space-x-2">
                <Calendar className="h-5 w-5 text-green-500" />
                <div className="text-sm">
                  <div>{reservation.startDate}</div>
                  <div>{reservation.endDate}</div>
                </div>
              </div>

              {/* Boutons d'action */}
              <div className="flex space-x-2">
                <button className="px-3 py-1 text-sm bg-blue-500 text-white rounded-md hover:bg-blue-600 transition-colors">
                  Modifier
                </button>
                <button className="px-3 py-1 text-sm bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors">
                  Supprimer
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ReservationList;
