// src/App.js
import React, { useState } from "react";
import { Card, CardHeader, CardTitle } from "./components/ui/card/card";
import Navigation from "./components/layout/Navigation";
import ReservationForm from "./components/reservation/ReservationForm";
import ReservationList from "./components/reservation/ReservationList";
import EditReservation from "./components/reservation/EditReservation";
import DeleteReservation from "./components/reservation/DeleteReservation";
import { Plus, Search, Edit, Trash2, Clock } from "lucide-react";

function App() {
  const [activeTab, setActiveTab] = useState("create");
  const [selectedReservationId, setSelectedReservationId] = useState(null);

  const handleEdit = (reservationId) => {
    setSelectedReservationId(reservationId);
    setActiveTab("edit");
  };

  const handleDelete = (reservationId) => {
    setSelectedReservationId(reservationId);
    setActiveTab("delete");
  };

  const renderContent = () => {
    switch (activeTab) {
      case "create":
        return <ReservationForm />;
      case "view":
        return <ReservationList onEdit={handleEdit} onDelete={handleDelete} />;
      case "edit":
        return selectedReservationId ? (
          <EditReservation reservationId={selectedReservationId} />
        ) : (
          <div className="p-6 text-center text-gray-500">
            Veuillez sélectionner une réservation à modifier dans l'onglet
            "Consulter"
          </div>
        );
      case "delete":
        return selectedReservationId ? (
          <DeleteReservation reservationId={selectedReservationId} />
        ) : (
          <div className="p-6 text-center text-gray-500">
            Veuillez sélectionner une réservation à supprimer dans l'onglet
            "Consulter"
          </div>
        );
      default:
        return <ReservationForm />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50">
      <Navigation />

      <main className="max-w-7xl mx-auto py-2 px-4">
        <Card className="w-full bg-white/80 backdrop-blur-sm">
          <CardHeader className="border-b border-purple-100">
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 text-transparent bg-clip-text">
                  Système de Réservation
                </CardTitle>
                <p className="text-gray-500 mt-1">
                  Gérez vos réservations d'hôtel en toute simplicité
                </p>
              </div>
              <div className="flex items-center text-gray-500">
                <Clock className="h-5 w-5 mr-2" />
                <span>{new Date().toLocaleDateString()}</span>
              </div>
            </div>
          </CardHeader>

          <div className="px-6 py-4">
            <div className="flex gap-3">
              <button
                onClick={() => {
                  setActiveTab("create");
                  setSelectedReservationId(null);
                }}
                className={`inline-flex items-center px-4 py-2 rounded-lg space-x-2 ${
                  activeTab === "create"
                    ? "bg-blue-600 text-white"
                    : "text-gray-600 bg-gray-100 hover:bg-gray-200"
                }`}
              >
                <Plus className="h-4 w-4" />
                <span>Nouvelle</span>
              </button>

              <button
                onClick={() => {
                  setActiveTab("view");
                  setSelectedReservationId(null);
                }}
                className={`inline-flex items-center px-4 py-2 rounded-lg space-x-2 ${
                  activeTab === "view"
                    ? "bg-blue-600 text-white"
                    : "text-gray-600 bg-gray-100 hover:bg-gray-200"
                }`}
              >
                <Search className="h-4 w-4" />
                <span>Consulter</span>
              </button>

              <button
                onClick={() => {
                  setActiveTab("edit");
                }}
                className={`inline-flex items-center px-4 py-2 rounded-lg space-x-2 ${
                  activeTab === "edit"
                    ? "bg-blue-600 text-white"
                    : "text-gray-600 bg-gray-100 hover:bg-gray-200"
                }`}
              >
                <Edit className="h-4 w-4" />
                <span>Modifier</span>
              </button>

              <button
                onClick={() => {
                  setActiveTab("delete");
                }}
                className={`inline-flex items-center px-4 py-2 rounded-lg space-x-2 ${
                  activeTab === "delete"
                    ? "bg-blue-600 text-white"
                    : "text-gray-600 bg-gray-100 hover:bg-gray-200"
                }`}
              >
                <Trash2 className="h-4 w-4" />
                <span>Supprimer</span>
              </button>
            </div>
          </div>

          <div className="transition-all duration-300 ease-in-out">
            {renderContent()}
          </div>
        </Card>
      </main>

      <footer className="py-4 text-center text-gray-600">
        <p>&copy; 2024 Système de Réservation d'Hôtel. Tous droits réservés.</p>
      </footer>
    </div>
  );
}

export default App;
