// src/components/reservation/DeleteReservation.jsx
import React, { useState } from "react";
import { Search, AlertTriangle } from "lucide-react";
import { Card, CardContent } from "../ui/card/card";

const DeleteReservation = () => {
  const [searchId, setSearchId] = useState("");
  const [reservationToDelete, setReservationToDelete] = useState(null);
  const [showConfirmation, setShowConfirmation] = useState(false);

  // Simuler la recherche d'une réservation
  const handleSearch = (e) => {
    e.preventDefault();
    // Simulation d'une réservation trouvée
    const foundReservation = {
      id: searchId,
      clientName: "John Doe",
      email: "john@example.com",
      roomType: "Double",
      startDate: "2024-12-01",
      endDate: "2024-12-05",
    };
    setReservationToDelete(foundReservation);
    setShowConfirmation(false);
  };

  const handleDelete = () => {
    console.log("Suppression de la réservation:", reservationToDelete.id);
    // Ici vous ajouterez l'appel API pour supprimer la réservation
    setReservationToDelete(null);
    setSearchId("");
    setShowConfirmation(false);
  };

  return (
    <div className="p-6">
      {/* Recherche de réservation */}
      <div className="mb-6">
        <form onSubmit={handleSearch} className="flex space-x-4">
          <div className="flex-1">
            <input
              type="text"
              placeholder="Entrez l'ID de la réservation"
              className="w-full p-3 border border-purple-100 rounded-xl"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
            />
          </div>
          <button
            type="submit"
            className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition-colors flex items-center space-x-2"
          >
            <Search className="h-4 w-4" />
            <span>Rechercher</span>
          </button>
        </form>
      </div>

      {/* Affichage des détails et confirmation */}
      {reservationToDelete && (
        <div className="space-y-6">
          <Card className="border-red-100">
            <CardContent className="p-6">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <span className="text-sm text-gray-600">Client</span>
                  <p className="font-medium">
                    {reservationToDelete.clientName}
                  </p>
                </div>
                <div className="space-y-2">
                  <span className="text-sm text-gray-600">Email</span>
                  <p className="font-medium">{reservationToDelete.email}</p>
                </div>
                <div className="space-y-2">
                  <span className="text-sm text-gray-600">Type de Chambre</span>
                  <p className="font-medium">{reservationToDelete.roomType}</p>
                </div>
                <div className="space-y-2">
                  <span className="text-sm text-gray-600">Dates</span>
                  <p className="font-medium">
                    {reservationToDelete.startDate} -{" "}
                    {reservationToDelete.endDate}
                  </p>
                </div>
              </div>

              {!showConfirmation ? (
                <button
                  onClick={() => setShowConfirmation(true)}
                  className="mt-6 w-full bg-red-500 text-white py-3 px-6 rounded-xl hover:bg-red-600 transition-colors flex items-center justify-center space-x-2"
                >
                  <AlertTriangle className="h-4 w-4" />
                  <span>Supprimer cette réservation</span>
                </button>
              ) : (
                <div className="mt-6 space-y-4">
                  <div className="p-4 bg-red-50 border border-red-200 rounded-lg">
                    <p className="text-red-700 text-center">
                      Êtes-vous sûr de vouloir supprimer cette réservation ?
                      Cette action est irréversible.
                    </p>
                  </div>
                  <div className="flex space-x-4">
                    <button
                      onClick={handleDelete}
                      className="flex-1 bg-red-500 text-white py-3 rounded-xl hover:bg-red-600 transition-colors"
                    >
                      Confirmer la suppression
                    </button>
                    <button
                      onClick={() => setShowConfirmation(false)}
                      className="flex-1 bg-gray-200 text-gray-700 py-3 rounded-xl hover:bg-gray-300 transition-colors"
                    >
                      Annuler
                    </button>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
};

export default DeleteReservation;
