// src/App.js
import React, { useState } from "react";
import Navigation from "./components/layout/Navigation";
import ReservationForm from "./components/reservation/ReservationForm";
import ReservationList from "./components/reservation/ReservationList";
import TabSection from "./components/reservation/TabSection";
import { Card, CardHeader, CardTitle } from "./components/ui/card/card";

function App() {
  const [activeTab, setActiveTab] = useState("create");

  const renderContent = () => {
    switch (activeTab) {
      case "create":
        return <ReservationForm />;
      case "view":
        return <ReservationList />;
      case "edit":
        // À implémenter
        return <div>Modification de réservation</div>;
      case "delete":
        // À implémenter
        return <div>Suppression de réservation</div>;
      default:
        return <ReservationForm />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50">
      <Navigation />
      <main className="max-w-7xl mx-auto py-8 px-4">
        <Card className="w-full bg-white/80 backdrop-blur-sm">
          <CardHeader className="border-b border-purple-100">
            <CardTitle className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 text-transparent bg-clip-text">
              Système de Réservation
            </CardTitle>
          </CardHeader>
          <TabSection activeTab={activeTab} setActiveTab={setActiveTab} />
          {renderContent()}
        </Card>
      </main>
    </div>
  );
}

export default App;
