// src/components/reservation/EditReservation.jsx
import React, { useState, useEffect } from "react";
import {
  User,
  Mail,
  Phone,
  Bed,
  Calendar,
  Clock,
  Heart,
  Search,
} from "lucide-react";
import { Card, CardContent } from "../ui/card/card";

const EditReservation = () => {
  const [searchId, setSearchId] = useState("");
  const [reservationToEdit, setReservationToEdit] = useState(null);
  const [formData, setFormData] = useState({
    clientName: "",
    email: "",
    phone: "",
    roomType: "",
    startDate: "",
    endDate: "",
    preferences: "",
  });

  // Simuler la recherche d'une réservation
  const handleSearch = (e) => {
    e.preventDefault();
    // Simulation d'une réservation trouvée
    const foundReservation = {
      id: searchId,
      clientName: "John Doe",
      email: "john@example.com",
      phone: "0612345678",
      roomType: "Double",
      startDate: "2024-12-01",
      endDate: "2024-12-05",
      preferences: "Vue sur mer",
    };
    setReservationToEdit(foundReservation);
    setFormData(foundReservation);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    console.log("Mise à jour:", formData);
    // Ici vous ajouterez l'appel API pour mettre à jour la réservation
  };

  return (
    <div className="p-6">
      {/* Recherche de réservation */}
      <div className="mb-6">
        <form onSubmit={handleSearch} className="flex space-x-4">
          <div className="flex-1">
            <input
              type="text"
              placeholder="Entrez l'ID de la réservation"
              className="w-full p-3 border border-purple-100 rounded-xl"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
            />
          </div>
          <button
            type="submit"
            className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition-colors flex items-center space-x-2"
          >
            <Search className="h-4 w-4" />
            <span>Rechercher</span>
          </button>
        </form>
      </div>

      {/* Formulaire de modification */}
      {reservationToEdit && (
        <form onSubmit={handleUpdate} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
                <User className="h-4 w-4 text-purple-500" />
                <span>Nom du Client</span>
              </label>
              <input
                type="text"
                name="clientName"
                value={formData.clientName}
                onChange={handleInputChange}
                className="w-full p-3 border border-purple-100 rounded-xl"
              />
            </div>

            <div className="space-y-2">
              <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
                <Mail className="h-4 w-4 text-purple-500" />
                <span>Email</span>
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                className="w-full p-3 border border-purple-100 rounded-xl"
              />
            </div>

            {/* Autres champs similaires */}

            <div className="space-y-2">
              <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
                <Bed className="h-4 w-4 text-purple-500" />
                <span>Type de Chambre</span>
              </label>
              <select
                name="roomType"
                value={formData.roomType}
                onChange={handleInputChange}
                className="w-full p-3 border border-purple-100 rounded-xl"
              >
                <option value="simple">Simple</option>
                <option value="double">Double</option>
                <option value="suite">Suite Luxe</option>
              </select>
            </div>

            <div className="space-y-2">
              <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
                <Calendar className="h-4 w-4 text-purple-500" />
                <span>Date d'arrivée</span>
              </label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleInputChange}
                className="w-full p-3 border border-purple-100 rounded-xl"
              />
            </div>

            <div className="space-y-2">
              <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
                <Clock className="h-4 w-4 text-purple-500" />
                <span>Date de départ</span>
              </label>
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleInputChange}
                className="w-full p-3 border border-purple-100 rounded-xl"
              />
            </div>
          </div>

          <div className="space-y-2">
            <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
              <Heart className="h-4 w-4 text-purple-500" />
              <span>Préférences</span>
            </label>
            <textarea
              name="preferences"
              value={formData.preferences}
              onChange={handleInputChange}
              className="w-full p-3 border border-purple-100 rounded-xl"
              rows="3"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-4 px-6 rounded-xl font-semibold hover:shadow-lg transform hover:scale-105 transition-all duration-200"
          >
            Mettre à jour la Réservation
          </button>
        </form>
      )}
    </div>
  );
};

export default EditReservation;
