// src/components/reservation/ReservationForm.jsx
import React from "react";
import { User, Mail, Phone, Bed, Calendar, Clock, Heart } from "lucide-react";
import { useReservation } from "../../hooks/useReservation";
import { Card, CardContent } from "../ui/card/card";

const FormField = ({ icon, label, name, type, value, onChange }) => (
  <div className="space-y-2">
    <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
      {icon}
      <span>{label}</span>
    </label>
    <input
      type={type}
      name={name}
      value={value}
      onChange={onChange}
      className="w-full p-3 border border-purple-100 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
      required
    />
  </div>
);

const ReservationForm = () => {
  const { formData, handleInputChange, handleSubmit } = useReservation();

  return (
    <Card>
      <CardContent className="pt-6">
        <form onSubmit={handleSubmit} className="space-y-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <FormField
              icon={<User className="h-4 w-4 text-purple-500" />}
              label="Nom du Client"
              name="clientName"
              type="text"
              value={formData.clientName}
              onChange={handleInputChange}
            />

            <FormField
              icon={<Mail className="h-4 w-4 text-purple-500" />}
              label="Email"
              name="email"
              type="email"
              value={formData.email}
              onChange={handleInputChange}
            />

            <FormField
              icon={<Phone className="h-4 w-4 text-purple-500" />}
              label="Téléphone"
              name="phone"
              type="tel"
              value={formData.phone}
              onChange={handleInputChange}
            />

            <div className="space-y-2">
              <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
                <Bed className="h-4 w-4 text-purple-500" />
                <span>Type de Chambre</span>
              </label>
              <select
                name="roomType"
                value={formData.roomType}
                onChange={handleInputChange}
                className="w-full p-3 border border-purple-100 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                required
              >
                <option value="simple">Simple</option>
                <option value="double">Double</option>
                <option value="suite">Suite Luxe</option>
              </select>
            </div>

            <FormField
              icon={<Calendar className="h-4 w-4 text-purple-500" />}
              label="Date d'arrivée"
              name="startDate"
              type="date"
              value={formData.startDate}
              onChange={handleInputChange}
            />

            <FormField
              icon={<Clock className="h-4 w-4 text-purple-500" />}
              label="Date de départ"
              name="endDate"
              type="date"
              value={formData.endDate}
              onChange={handleInputChange}
            />
          </div>

          <div className="space-y-2">
            <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
              <Heart className="h-4 w-4 text-purple-500" />
              <span>Préférences Spéciales</span>
            </label>
            <textarea
              name="preferences"
              value={formData.preferences}
              onChange={handleInputChange}
              className="w-full p-3 border border-purple-100 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
              rows="3"
              placeholder="Précisez vos demandes particulières..."
            />
          </div>

          <button
            type="submit"
            className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-4 px-6 rounded-xl font-semibold hover:shadow-lg transform hover:scale-105 transition-all duration-200"
          >
            Confirmer la Réservation
          </button>
        </form>
      </CardContent>
    </Card>
  );
};

export default ReservationForm;
