// src/components/reservation/ReservationList.jsx
import React from "react";
import { Card, CardContent } from "../ui/card/card";
import { Calendar, User, Bed } from "lucide-react";

const ReservationList = () => {
  // Simuler des données de réservation
  const reservations = [
    {
      id: 1,
      clientName: "John Doe",
      roomType: "double",
      startDate: "2024-12-01",
      endDate: "2024-12-05",
    },
    // Ajoutez plus de réservations ici
  ];

  return (
    <Card>
      <CardContent className="space-y-4">
        {reservations.map((reservation) => (
          <div
            key={reservation.id}
            className="p-4 border border-purple-100 rounded-lg hover:border-purple-500 transition-all"
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <User className="h-5 w-5 text-purple-500" />
                <span className="font-medium">{reservation.clientName}</span>
              </div>
              <div className="flex items-center space-x-4">
                <Bed className="h-5 w-5 text-blue-500" />
                <span>{reservation.roomType}</span>
              </div>
              <div className="flex items-center space-x-2">
                <Calendar className="h-5 w-5 text-green-500" />
                <span>
                  {reservation.startDate} - {reservation.endDate}
                </span>
              </div>
            </div>
          </div>
        ))}
      </CardContent>
    </Card>
  );
};

export default ReservationList;
